function login(){var e=document.getElementById("email_field").value,t=document.getElementById("password_field").value;firebase.auth().signInWithEmailAndPassword(e,t).catch(function(e){var n=(e.code,e.message);window.alert("Error "+n)})}function signup(){var e=document.getElementById("inputName").value,t=document.getElementById("inputEmail").value,n=document.getElementById("inputPassword").value,a=document.getElementById("inputArea").value,l=document.getElementById("inputGender").value,d=document.getElementById("inputCity").value,i=document.getElementById("inputPostalAddress").value,r=document.getElementById("inputPhone").value;if(e.length<10)window.alert("username is too small");else if("Enter your city"===d)window.alert("city is not defined");else if("Select your gender"===l)window.alert("Gender is not defined");else if(a.length<2)window.alert("Area is not defined");else if(r.length<11)window.alert("invalid number");else if(i.length<1)window.alert("invalid postal address");else{firebase.auth().createUserWithEmailAndPassword(t,n).catch(function(e){var n=(e.code,e.message);window.alert("Error "+n)});var s=firebase.auth().currentUser,o=s.uid;writeUserData(o,e,t,n,a,d,l,r,i),window.location.href="#profile_details"}}function writeUserData(e,t,n,a,l,d,i,r,s){firebase.database().ref("users/"+e).set({name:t,email:n,password:a,Address:l,gender:i,phone:r,postaladdress:s,city:d})}firebase.auth().onAuthStateChanged(function(e){if(e){document.getElementById("login1").style.display="none",document.getElementById("signup").style.display="none",document.getElementById("profile_details").style.display="block";var e=firebase.auth().currentUser;if(null!=e){var t=e.name;console.log(t)}}else document.getElementById("login1").style.display="block",document.getElementById("signup").style.display="block",document.getElementById("profile_details").style.display="none"});